import React, { Component, useState } from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Fab from "@material-ui/core/Fab";
import Help from "@material-ui/icons/Help";
import GetApp from "@material-ui/icons/GetApp";
import GridOn from "@material-ui/icons/GridOn";
import Pageview from "@material-ui/icons/Pageview";
import OpenInBrowser from "@material-ui/icons/OpenInBrowser";
import EventNote from "@material-ui/icons/EventNote";
import Launch from "@material-ui/icons/Launch";
import Icon from "@material-ui/core/Icon";
import SpeedDial from "@material-ui/lab/SpeedDial";
import SpeedDialIcon from "@material-ui/lab/SpeedDialIcon";
import SpeedDialAction from "@material-ui/lab/SpeedDialAction";
import FileCopyIcon from "@material-ui/icons/FileCopyOutlined";
import SaveIcon from "@material-ui/icons/Save";
import Grid from "@material-ui/core/Grid";
import Publish from "@material-ui/icons/Publish";
import Tooltip from "@material-ui/core/Tooltip";

const useStyles = makeStyles(theme => ({
  fab: {
    margin: theme.spacing(0.5),
    marginLeft: theme.spacing(-0.5)
  },
  speedDownload: {
    margin: theme.spacing(0.5),
    marginLeft: theme.spacing(-0.5),
    marginRight: theme.spacing(-0.5)
  },
  speedLaunch: {
    margin: theme.spacing(0.5),
    marginRight: theme.spacing(-0.5)
  },
  extendedIcon: {
    marginRight: theme.spacing(1)
  },
  tooltip: {
    marginTop: "-.01rem",
    fontSize: "1.5rem"
  }
}));
const actionsDownload = [
  {
    icon: <GridOn />,
    name: "About",
    bgColor: "#08C2FF",
    operation: "crrExport"
  },
  {
    icon: <EventNote />,
    name: "Projects",
    bgColor: "#ACD6F6",
    operation: "rawExport"
  }
];

const SpeedDialCus = props => {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [openDownload, setOpenDownload] = React.useState(false);

  const [hidden, setHidden] = React.useState(false);
  const [hiddenDownload, setHiddenDownload] = React.useState(false);
  function handleClick(e, operation) {
    console.log("OUTPUT: handleClick =====-> operation", operation);
    e.preventDefault();
    if (operation == "PageviewIcon") {
    } else if (operation == "network") {
      window.open("https://www.google.com");
    }
    setOpen(!open);
  }

  function handleOpen() {
    if (!hidden) {
      setOpen(true);
    }
  }

  function handleClose() {
    setOpen(false);
  }
  //====
  function handleClickDownload(e, operation) {
    console.log("OUTPUT: handleClickDownload -> option", operation);
    e.preventDefault();
    if (operation == "GridOnIcon") {
    } else if (operation == "EventNoteIcon") {
    }
    setOpenDownload(!open);
  }

  function handleOpenDownload() {
    if (!hiddenDownload) {
      setOpenDownload(true);
    }
  }

  function handleCloseDownload() {
    setOpenDownload(false);
  }
  return (
    <div>
      <Grid container alignContent="center" direction="row-reverse">
        <SpeedDial
          className={classes.speedDownload}
          ariaLabel="SpeedDial tooltip example"
          hidden={hidden}

          icon={<GetApp />}
          ButtonProps={{ size: "small", style: { backgroundColor: "#0102F7" } }}
          onBlur={handleCloseDownload}
          onClick={handleClickDownload}
          onClose={handleCloseDownload}
          onFocus={handleOpenDownload}
          onMouseEnter={handleOpenDownload}
          onMouseLeave={handleCloseDownload}
          open={openDownload}
        >
          {actionsDownload.map(action => (
            <SpeedDialAction
              key={action.name}
              icon={action.icon}
              ButtonProps={{
                size: "small",
                style: { backgroundColor: action.bgColor, color: "white" }
              }}
              tooltipTitle={action.name}
              tooltipOpen
              TooltipClasses={classes}
              value={action.icon}
              onClick={e => {
                handleClickDownload(e, action.bgColor);
              }}
            />
          ))}
        </SpeedDial>
      </Grid>
    </div>
  );
};

export default SpeedDialCus;
