import React from 'react';
import { createStyles, Theme, withStyles, WithStyles, makeStyles, useTheme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography';
import style from './popup.module.scss';
import DialogContentText from '@material-ui/core/DialogContentText';
import { useMediaQuery, DialogContent, DialogTitle, DialogActions } from '@material-ui/core';
import EmailFields from '../email_fields/email_fields';

const styles = (theme: Theme) =>
  createStyles({
    root: {
      margin: 0,
      padding: theme.spacing(2),
    },
    closeButton: {
      position: 'absolute',
      right: theme.spacing(1),
      top: theme.spacing(1),
      color: theme.palette.grey[500],
    },
    dialog: {
        position: 'fixed',
        left: 10,
        top: 50
      }
  });
  
  const useStyles = makeStyles({
    dialog: {
 
      position: 'absolute',
      left: 10,
      top: 50
    }
  });

  
  export default function ResponsiveDialog()  {
 

const [open, setOpen] = React.useState(false);
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down('sm'));

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


  return (
    <div className={style.fixed}>
       <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Open responsive dialog
      </Button>
      <Dialog

        fullScreen={fullScreen}
        open={open}
        onClose={handleClose}
        aria-labelledby="responsive-dialog-title"
        PaperProps={{
            style: {
                zIndex:9999,
              backgroundColor: 'rgba(255,255,255)',
              boxShadow: 'none',
              backdropFilter: 'blur(10px)'
            },
          }}
      >
        <DialogTitle id="responsive-dialog-title">{"Use Google's location service?"}</DialogTitle>
        <DialogContent>
        <EmailFields></EmailFields>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose} color="primary">
            Discard
          </Button>
         
        </DialogActions>
      </Dialog>
      
    </div>
  );
}
