import Link from 'next/link'
import styled from 'styled-components'
import styles from './NavBar.module.scss'
import { useMediaQuery } from 'react-responsive';
import React from 'react';
//import  '../../state/responsive_controller';
function myFunction() {
  let x:HTMLElement  = document.getElementById("myTopnav");
  if (x.className === "topnav") {
    x.className += " responsive";
  } else {
    x.className = "topnav";
  }
}


const DesNav = () => (
  <div>
  <div className={styles.topFixed} >
    <div >
      
    </div>
    <div className={styles.sticky}>
      <div className={styles.firstBar}>
      <Link href='/'><div className={styles.h3}>yuhangang</div></Link>
      <div className={styles.container}></div>
     
        <Link href='/about' prefetch><div className={styles.secondBarButtons}>About</div></Link>
        <Link href='/contact' prefetch><div className={styles.secondBarButtons}>Projects</div></Link>
        <a href="javascript: document.body.scrollIntoView({behavior: 'smooth', block: 'end', inline: 'nearest'});" className={styles.secondBarButtons}>Contact</a>
        
        
      </div>  
      <div className={styles.secondBar}>
        
      </div>
    </div>


  </div>
  </div>
  
)
const MobileNav = () => (
 
  <div className={styles.topFixed} >
    <div >
    <Link href='/'><div className={styles.h3}>yuhangang</div></Link>
    <div className={styles.topnav} id="myTopnav">
   
  
</div>


  </div>
  </div>
  
)

const NavNew =()=>{
  const [width, setWidth] = React.useState(window.innerWidth);
  const breakpoint = 620;

  React.useEffect(() => {
    /* Inside of a "useEffect" hook add an event listener that updates
       the "width" state variable when the window size changes */
    window.addEventListener("resize", () => setWidth(window.innerWidth));

    /* passing an empty array as the dependencies of the effect will cause this
       effect to only run when the component mounts, and not each time it updates.
       We only want the listener to be added once */
  }, []);

  return width < breakpoint ? <MobileNav/> : <DesNav/>;
}

export default NavNew;
