import { useState } from 'react';
import styles from './email_fields.module.scss';

const EmailField = () => {
  const [contact, setContact] = useState({
    name: '',
    email: '',
    subject: 'StaticForms - Contact Form',
    honeypot: '', // if any value received in this field, form submission will be ignored.
    message: '',
    replyTo: '@', // this will set replyTo of email to email address entered in the form
    accessKey: 'you-access-key' // get your access key from https://www.staticforms.xyz
  });

  const [response, setResponse] = useState({
    type: '',
    message: ''
  });

  const handleChange = e =>
    setContact({ ...contact, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await fetch('https://api.staticforms.xyz/submit', {
        method: 'POST',
        body: JSON.stringify(contact),
        headers: { 'Content-Type': 'application/json' }
      });

      const json = await res.json();

      if (json.success) {
        setResponse({
          type: 'success',
          message: 'Thank you for reaching out to us.'
        });
      } else {
        setResponse({
          type: 'error',
          message: json.message
        });
      }
    } catch (e) {
      console.log('An error occurred', e);
      setResponse({
        type: 'error',
        message: 'An error occured while submitting the form'
      });
    }
  };
  return (
    
<div>
<form className={styles.cf}>
  <div className={`${styles.half}${ styles.left}${styles.cf}`}>
    <input type="text" id="input-name" placeholder="Name"></input>
    <input type="email" id="input-email" placeholder="Email address"></input>
    <input type="text" id="input-subject" placeholder="Subject"></input>
  </div>
  <div className={`${styles.half}${ styles.left}${styles.cf}`}>
    <textarea name="message"  id="input-message" placeholder="Message"></textarea>
  </div>  
  <input type="submit" value="Submit" id="input-submit"></input>
</form>
</div>
   
  );
};

export default EmailField;
