import React, { Component } from "react";
import styles from "./scrollToTopButton.module.scss";

import { makeStyles, createStyles, Theme, createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import SpeedDial from '@material-ui/lab/SpeedDial';
import SpeedDialIcon from '@material-ui/lab/SpeedDialIcon';
import SpeedDialAction from '@material-ui/lab/SpeedDialAction';
import FileCopyIcon from '@material-ui/icons/FileCopyOutlined';
import SaveIcon from '@material-ui/icons/Save';
import PrintIcon from '@material-ui/icons/Print';
import ShareIcon from '@material-ui/icons/Share';
import FavoriteIcon from '@material-ui/icons/Favorite';
import EditIcon from '@material-ui/icons/Edit';
import SpeedDialCus from "./speedDialAction";
import CloseRoundedIcon from '@material-ui/icons/CloseRounded';
import MenuRoundedIcon from '@material-ui/icons/MenuRounded';
import InfoIcon from '@material-ui/icons/Info';
import HomeIcon from '@material-ui/icons/Home';
import MailIcon from '@material-ui/icons/Mail';
import ViewCompactIcon from '@material-ui/icons/ViewCompact';
import Box from "@material-ui/core/Box";
import Link from "next/link";
import useScrollTrigger from "@material-ui/core/useScrollTrigger";
import CssBaseline from "@material-ui/core/CssBaseline";
import Fab from "@material-ui/core/Fab";
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import Zoom from '@material-ui/core/Zoom';

interface Props {
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window?: () => Window;
  children: React.ReactElement;
}



function ScrollTop(props: Props) {
  const { children, window } = props;
  const classes = useStyles();
  // Note that you normally won't need to set the window ref as useScrollTrigger
  // will default to window.
  // This is only being set here because the demo is in an iframe.
  const trigger = useScrollTrigger({
    target: window ? window() : undefined,
    disableHysteresis: true,
    threshold: 100,
  });

  const handleClick = (event: React.MouseEvent<HTMLDivElement>) => {
    ScrollToTop1();
  };

  return (
    <Zoom in={trigger}>
      <div onClick={handleClick} role="presentation" className={classes.root}>
        {children}
      </div>
    </Zoom>
  );
}

function BackToTop(props: Props) {
  return (

      <ScrollTop {...props}>
        <Fab color="secondary" size="small" aria-label="scroll back to top">
          <KeyboardArrowUpIcon />
        </Fab>
      </ScrollTop>

  );
}

 function ScrollToTop1() {

  const scrollTo= function () {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  }

  

    return (

      <div className={styles.scrollToTop}>
        <div className={styles.buttonStyle}>
        <div className="scroll-to-top">
        
          <div onClick={() => scrollTo()} className={styles.floating_button}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="black" width="2rem" height="2rem"><path d="M0 0h24v24H0z" fill="none"/><path d="M7.41 15.41L12 10.83l4.59 4.58L18 14l-6-6-6 6z"/></svg>
          </div>
          </div>
      </div>
      
        </div>
        
  
      
    );
  }


  const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    
    root: {
      
      height: 380,
      transform: 'translateZ(0px)',
      flexGrow: 1,
    },
    speedDial: {
      position: 'absolute',
      bottom: theme.spacing(2),
      right: theme.spacing(2),
    },
  }),
);

const actions = [
  { icon: <Link href='/about' prefetch><MailIcon /></Link>, name: 'Contact' },
  { icon: <ViewCompactIcon></ViewCompactIcon>, name: 'Projects' },
  
  { icon: <Link href='/about' prefetch><InfoIcon /></Link>, name: 'About' },
  { icon: <Link href='/' prefetch><HomeIcon /></Link>, name: 'Home' },
];

const theme = createMuiTheme({
  overrides: {
    MuiButton: {
      root: {
        margin: "5rem",

        position: "fixed",
      }
    }
  },
  palette: {
    primary: {
      main: '#FFFFFFCC',
      light:'#666666',

    },
    secondary: {main:'#222222'},
  },
});




export default function ScrollToTop() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [hidden, setHidden] = React.useState(false);

  const handleVisibility = () => {
    setHidden((prevHidden) => !prevHidden);
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  
  return (
    <div className={styles.bottomRight}>

<div  className={styles.SpeedDial}>
      
      <ThemeProvider theme={theme}  >
        
      
      <Box>
      <SpeedDial
        color="light"
        ariaLabel="SpeedDial openIcon example"
        className={classes.speedDial}
        hidden={hidden}
        icon={<MenuRoundedIcon/>}
        openIcon={<CloseRoundedIcon/>}
        onClose={handleClose}
        onOpen={handleOpen}
        open={open}
        
        
      >
        {actions.map((action) => (
          <SpeedDialAction
            color="secondary"
            key={action.name}
            icon={action.icon}
            tooltipOpen
            tooltipTitle={action.name}
            onClick={handleClose}
          />
        ))}
      </SpeedDial>
      </Box>
    
    </ThemeProvider>
            
    </div>
    <div  className={styles.BackButton}>
      
      <ThemeProvider  theme={theme}  >
        
    
      <Box>
        <ScrollTop>
        <Fab  color="primary"  aria-label="scroll back to top">
          <KeyboardArrowUpIcon />
        </Fab>
      </ScrollTop>

      </Box>
      

    </ThemeProvider>
            
    </div>
    </div>
    
    
  );
        }

