import Head from 'next/head'
import styles from './layout.module.scss'
import ScrollToTop from './scrollToTopButton/scrollToTopButton';
import utilStyles from '../styles/utils.module.css'
import Nav from './navBar/NavBar'
import Link from 'next/link'
import { title } from 'process'
import ProfileLink from './aboutContainer/profile_link/profile_link'
import React from 'react';
import EmailFields from '../components/email_fields/email_fields'

const name = 'Yuhangang'
export const siteTitle = 'yuhangang'

export default function Layout({
  children,
  home
}: {
  children: React.ReactNode
  home?: boolean
}) {
  return (
    <div className={styles.background}>

      <Nav></Nav>


      <div className={styles.container}>


        <header className={styles.header}>

          {home ? (
            <>


            </>
          ) : (
              <>

                <h2 className={utilStyles.headingLg}>
                  <Link href="/">
                    <a className={utilStyles.colorInherit}>{name}</a>
                  </Link>
                </h2>
              </>
            )}
        </header>
        <main>{children}</main>
        
      </div>
      
      <ScrollToTop/>
    </div>
  )
}
