import React, { Component } from "react";
import styles from "./scrollToTopButton.module.scss";
import { useMediaQuery } from 'react-responsive'

export default function ScrollToTop1() {
  const scrollTo= function () {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  }
  const isPortrait = useMediaQuery({ query: '(orientation: portrait)' })
  const isSrollable = useMediaQuery(
    { minDeviceWidth: 1224 },
    { deviceWidth: 1600 } // `device` prop
 )

    return (
      <div className={styles.expanded}>
        {isPortrait && window.pageYOffset > 300 ? <div></div> :<div className={styles.buttonStyle}>
        <div className="scroll-to-top">
        
          <div onClick={() => scrollTo()}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="black" width="2rem" height="2rem"><path d="M0 0h24v24H0z" fill="none"/><path d="M7.41 15.41L12 10.83l4.59 4.58L18 14l-6-6-6 6z"/></svg>
          </div>
          </div>
      </div>
      }
        </div>
        
  
      
    );
  }


class ScrollToTop extends Component {
  constructor(props) {
    super(props);
    this.state = {
      is_visible: false
    };
  }

  componentDidMount() {
    var scrollComponent = this;
    document.addEventListener("scroll", function(e) {
      scrollComponent.toggleVisibility();
    });
  }
  toggleVisibility() {
    if (window.pageYOffset > 300) {
      this.setState({
        is_visible: true
      });
    } else {
      this.setState({
        is_visible: false
      });
    }
  }

  scrollToTop() {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  }

  render() {
    const  is_visible  = this.state;
    return (
      <div className={styles.expanded}>
        <div className={styles.buttonStyle}>
        <div className="scroll-to-top">
        {is_visible && (
          <div onClick={() => this.scrollToTop()}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="black" width="2rem" height="2rem"><path d="M0 0h24v24H0z" fill="none"/><path d="M7.41 15.41L12 10.83l4.59 4.58L18 14l-6-6-6 6z"/></svg>
          </div>
        )}
      </div>
        </div>
        
      </div>
      
    );
  }
}
