import React, { Component } from "react";
import styles from "./scrollToTopButton.module.scss";
import { useMediaQuery } from 'react-responsive';
import { makeStyles, createStyles, Theme, createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import SpeedDial from '@material-ui/lab/SpeedDial';
import SpeedDialIcon from '@material-ui/lab/SpeedDialIcon';
import SpeedDialAction from '@material-ui/lab/SpeedDialAction';
import FileCopyIcon from '@material-ui/icons/FileCopyOutlined';
import SaveIcon from '@material-ui/icons/Save';
import PrintIcon from '@material-ui/icons/Print';
import ShareIcon from '@material-ui/icons/Share';
import FavoriteIcon from '@material-ui/icons/Favorite';
import EditIcon from '@material-ui/icons/Edit';
import ListRoundedIcon from '@material-ui/icons/ListRounded';
import CloseRoundedIcon from '@material-ui/icons/CloseRounded';

 function ScrollToTop1() {

  const scrollTo= function () {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  }
  const isPortrait:boolean = useMediaQuery({ query: '(orientation: portrait)' })
  

    return (
      <div >
        {isPortrait ? ScrollToTop1() :<div className={styles.buttonStyle}>
        <div className="scroll-to-top">
        
          <div onClick={() => scrollTo()} className={styles.floating_button}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="black" width="2rem" height="2rem"><path d="M0 0h24v24H0z" fill="none"/><path d="M7.41 15.41L12 10.83l4.59 4.58L18 14l-6-6-6 6z"/></svg>
          </div>
          </div>
      </div>
      }
        </div>
        
  
      
    );
  }


  const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    
    root: {
      
      height: 380,
      transform: 'translateZ(0px)',
      flexGrow: 1,
    },
    speedDial: {
      position: 'absolute',
      bottom: theme.spacing(2),
      right: theme.spacing(2),
    },
  }),
);

const actions = [
  { icon: <div></div>, name: 'Copy' },
  { icon: <SaveIcon />, name: 'Save' },
  { icon: <PrintIcon />, name: 'Print' },
  { icon: <ShareIcon />, name: 'Share' },
  { icon: <FavoriteIcon />, name: 'Like' },
];

const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#FFFFFF',
    },
    secondary: {main:'#FFFFFF'},
  },
});


export default function OpenIconSpeedDial() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [hidden, setHidden] = React.useState(false);

  const handleVisibility = () => {
    setHidden((prevHidden) => !prevHidden);
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div  className={styles.expanded}>
   
      <ThemeProvider theme={theme}  >
        
      <Button onClick={handleVisibility}
      
      ></Button>
      <SpeedDial

        ariaLabel="SpeedDial openIcon example"
        className={classes.speedDial}
        hidden={hidden}
        icon={<ListRoundedIcon openIcon={<CloseRoundedIcon/>} />}
        onClose={handleClose}
        onOpen={handleOpen}
        open={open}
        
      >
        {actions.map((action) => (
          <SpeedDialAction
            
            key={action.name}
            icon={action.icon}
            tooltipOpen
            tooltipTitle={action.name}
            onClick={handleClose}
          />
        ))}
      </SpeedDial>
    </ThemeProvider>
    </div>
    
  );
        }

