import React from 'react';
import styles from './email_fields.module.scss';

import { makeStyles, Theme, createStyles,withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';
import InputBase from '@material-ui/core/InputBase';
import TextField from '@material-ui/core/TextField';


const BootstrapInput = withStyles((theme: Theme) =>
  createStyles({
    root: {
      'label + &': {
        marginTop: theme.spacing(3),
      },
    },
    input: {
      width:'10rem',
      borderRadius: 4,
      position: 'relative',
      backgroundColor:'#FFFFFF10',
      border: '1px solid #ced4da',
      fontSize: 16,
      color:'#FFFFFF80',
      padding: '10px 26px 10px 12px',
      transition: theme.transitions.create(['border-color', 'box-shadow']),
      // Use the system font instead of the default Roboto font.
      fontFamily: [
        '-apple-system',
        'BlinkMacSystemFont',
        '"Segoe UI"',
        'Roboto',
        '"Helvetica Neue"',
        'Arial',
        'sans-serif',
        '"Apple Color Emoji"',
        '"Segoe UI Emoji"',
        '"Segoe UI Symbol"',
      ].join(','),
      '&:focus': {
        borderRadius: 4,
        borderColor: '#80bdff',
        boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
      },
    },
  }),
)(InputBase);

const StyledTextField=withStyles((theme: Theme) =>
createStyles({
  root: {
    'label + &': {
      marginTop: theme.spacing(3),
    },
  },
  input: {
    width:'10rem',
    borderRadius: 4,
    position: 'relative',
    backgroundColor:'#FFFFFF10',
    border: '1px solid #ced4da',
    fontSize: 16,
    color:'#FFFFFF80',
    padding: '10px 26px 10px 12px',
    transition: theme.transitions.create(['border-color', 'box-shadow']),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
    '&:focus': {
      borderRadius: 4,
      borderColor: '#80bdff',
      boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
    },
  },
}),
)(TextField)

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    button: {
      display: 'block',
      marginTop: theme.spacing(2),
    },
    formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
    },
  }),
);


const EmailField = () => {
  const classes = useStyles();
  const [age, setAge] = React.useState<string | number>('');
  const [open, setOpen] = React.useState(false);
  const[amount,setAmount]= React.useState<string | number>('');

  const handleChange = (event: React.ChangeEvent<{ value: unknown }>) => {
    setAge(event.target.value as number);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };
  
  return (
    
<div>

<input className={styles.textfield} type="text" id="input-name" placeholder="Name"></input>

<select className={styles.textfield} id="country" name="country/region">
      <option className={styles.select_option} value="australia">Australia</option>
      <option className={styles.select_option} value="canada">Canada</option>
      <option className={styles.select_option} value="china">China</option>
      <option className={styles.select_option} value="usa">USA</option>

    </select>
    <br></br>
    <textarea className={styles.contenttexfield} rows={5} cols={5} id="input-email" placeholder="Content"></textarea>
  
    <br></br>
    <button className={styles.button}>SEND</button>
</div>
   
  );
};

export default EmailField;
