import React, { Component } from "react";
import styles from "./scrollToTopButton.module.scss";
import { useMediaQuery } from 'react-responsive'

export default function ScrollToTop() {

  const scrollTo= function () {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  }
  const isPortrait:boolean = useMediaQuery({ query: '(orientation: portrait)' })
  

    return (
      <div className={styles.expanded}>
        {isPortrait ? ScrollToTop1() :<div className={styles.buttonStyle}>
        <div className="scroll-to-top">
        
          <div onClick={() => scrollTo()}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="black" width="2rem" height="2rem"><path d="M0 0h24v24H0z" fill="none"/><path d="M7.41 15.41L12 10.83l4.59 4.58L18 14l-6-6-6 6z"/></svg>
          </div>
          </div>
      </div>
      }
        </div>
        
  
      
    );
  }


  function ScrollToTop1() {
    
    const isPortrait = useMediaQuery({ query: '(orientation: portrait)' })
    const isSrollable = useMediaQuery(
      { minDeviceWidth: 1224 },
      { deviceWidth: 1600 } // `device` prop
   )
  
      return (
        <div id="container-floating">

  <div className={`${styles.nd4}${styles.nds}`}><img className={styles.reminder} src="//ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_reminders_speeddial_white_24dp.png" /></div>
  <div className={`${styles.nd4}${styles.nds}${styles.is_always_opened}`}><img className={styles.reminder} src="//ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_reminders_speeddial_white_24dp.png" /></div>
  <div className={`${styles.nd4}${styles.nds}`}><img className={styles.reminder} src="//ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_reminders_speeddial_white_24dp.png" /></div>
  <div className={`${styles.nd4}${styles.nds}`}><img className={styles.reminder} src="//ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/ic_reminders_speeddial_white_24dp.png" /></div>

  <div id="floating-button">
    <p className={styles.plus}>+</p>
    <img className={styles.close} src="https://ssl.gstatic.com/bt/C3341AA7A1A076756462EE2E5CD71C11/1x/bt_compose2_1x.png"></img>
  </div>

</div>
          
    
        
      );
    }
